import * as React from 'react';
import Router from 'grey-app-react-router/Router';

export interface ILinkProps {
    to: string;
    state?: any;
}

export default class Link extends React.PureComponent<ILinkProps>{
    public render() {
        return <a href={this.props.to} onClick={this.onClickHandler} >{this.props.children}</a>
    }

    private onClickHandler = (e: React.MouseEvent<HTMLAnchorElement, MouseEvent>) => {
        e.preventDefault();
        Router.history.push(this.props.to, this.props.state);
    }
}