import * as React from 'react';

export interface IPageBasisProps {

    /** 获取真实对象的方法 */
    refReal?: (e: PageBasis) => void;
}

export default abstract class PageBasis<P extends IPageBasisProps = any, S=any> extends React.PureComponent<P, S> {
    constructor(props: P) {
        super(props);

        // 输出真实实例
        if (this.props.refReal) {
            this.props.refReal(this);
        }
    }

    /** 重从缓存被重新渲染 */
    public renewRender(){
        // 空
    }
}