import * as React from 'react';
import Router from 'grey-app-react-router/Router';

interface IRedirectProps{
    path: string; 
    state?: any;
}

export default class Redirect extends React.PureComponent<IRedirectProps> {

    public componentDidMount() {
        Router.history.push(this.props.path, this.props.state);
    }

    public render() {
        return null;
    }
}